// src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import Navbar from './Components/Navbar/Navbar';
import Footer from './Components/Footer/Footer';
import Home from './Views/Home';
import Jugadores from './Views/Jugadores';
import Entrenadores from './Views/Entrenadores';
import Equipos from './Views/Equipos';
import Perfil from './Views/PerfilJugador';
import Login from './Views/Login';

function App() {
  const { user } = useAuth();

  return (
    <div>
      <Navbar />
      <Routes>
        <Route path="/login" element={!user ? <Login /> : <Navigate replace to="/" />} />
        <Route path="/" element={user ? <Home /> : <Navigate replace to="/login" />} />
        <Route path="/jugadores" element={user ? <Jugadores /> : <Navigate replace to="/login" />} />
        <Route path="/entrenadores" element={user ? <Entrenadores /> : <Navigate replace to="/login" />} />
        <Route path="/equipos" element={user ? <Equipos /> : <Navigate replace to="/login" />} />
        <Route path="/perfil" element={user ? <Perfil /> : <Navigate replace to="/login" />} />
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
