import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './Context/AuthContext';
import Navbar from './Components/Navbar/Navbar';
import Footer from './Components/Footer/Footer';
import Home from './Views/Home';
import Jugadores from './Views/Jugadores';
import Entrenadores from './Views/Entrenadores';
import Equipos from './Views/Equipos';
import Perfil from './Views/PerfilJugador';
import Login from './Views/Login';

function App() {
  const { isAuthenticated } = useAuth();

  return (
    <Router>
      <div>
        {isAuthenticated && <Navbar />}
        <Routes>
          <Route path="/login" element={!isAuthenticated ? <Login /> : <Navigate to="/" />} />
          <Route path="/" element={isAuthenticated ? <Home /> : <Navigate to="/login" />} />
          <Route path="/jugadores" element={isAuthenticated ? <Jugadores /> : <Navigate to="/login" />} />
          <Route path="/entrenadores" element={isAuthenticated ? <Entrenadores /> : <Navigate to="/login" />} />
          <Route path="/equipos" element={isAuthenticated ? <Equipos /> : <Navigate to="/login" />} />
          <Route path="/perfil" element={isAuthenticated ? <Perfil /> : <Navigate to="/login" />} />
        </Routes>
        {isAuthenticated && <Footer />}
      </div>
    </Router>
  );
}

export default App;
