import React, { useState, useEffect } from 'react';
import Anuncio from './AnuncioComponente/Anuncio';

const TablonAnuncios = ({ categoria }) => {
  const [anuncios, setAnuncios] = useState([]);

  useEffect(() => {
    // Aquí iría la lógica para cargar los anuncios de la base de datos
    // Esto es solo un ejemplo de cómo podrías cargar los datos
    fetch(`/api/anuncios/${categoria}`)
      .then(res => res.json())
      .then(data => setAnuncios(data))
      .catch(error => console.error('Error:', error));
  }, [categoria]);

  return (
    <div className="tablon">
      {anuncios.map(anuncio => (
        <Anuncio key={anuncio.id} anuncio={anuncio} />
      ))}
    </div>
  );
};

export default TablonAnuncios;
